var path = require("path")
switch (process.platform) {
    case 'win32':
        {
            module.exports.location = "phantom/bin/win32/phantomjs.exe"
            module.exports.platform = "win32"
            module.exports.arch = "x64"
            break;
        }
    case 'darwin':
        {
            module.exports.location = "phantom/bin/darwin/phantomjs"
            module.exports.platform = "darwin"
            module.exports.arch = "x64"
            break;
        }
    case 'linux':
    default:
        {
            module.exports.location = "phantom/bin/linux/phantomjs"
            module.exports.platform = "linux"
            module.exports.arch = "x64"
            break;
        }
}